import React from "react";
import Auth from "../../utils/auth";
import { Link } from "react-router-dom";

const backgroundImageStyle = {
  backgroundRepeat: "no-repeat",
  backgroundSize: "cover",
  minHeight:"50%"
}

function Nav() {

  function showNavigation() {
    if (Auth.loggedIn()) {
      return (
        <ul className="flex-row">
          <li className="mx-1">
            <Link to="/orderHistory">
              Fuzzies Found
            </Link>
          </li>
          <li className="mx-1">
            {/* this is not using the Link component to logout or user and then refresh the application to the start */}
            <a href="/" onClick={() => Auth.logout()}>
              Logout
            </a>
          </li>
        </ul>
      );
    } else {
      return (
        <ul className="flex-row">
          <li className="mx-1">
            <Link to="/signup">
              Signup
            </Link>
          </li>
          <li className="mx-1">
            <Link to="/login">
              Login
            </Link>
          </li>
        </ul>
      );
    }
  }

  return (
    <header className="flex-row px-1">
      <div id="titleandnav">
        <h1>
          <Link to="/">
            <span role="img" aria-label="shopping bag">üê∂</span>
            FuzzyFinder
          </Link>
          <span id="desc">Your personal therapy dog finder!</span>
        </h1>

        <nav>
          {showNavigation()}
        </nav>
      </div>

      {/* <div>
        <img src="/images/background1-friends4ever.jpg" id="headerBackground" alt="dog-human-friendship" style={backgroundImageStyle} />
      </div> */}
    </header>

  );
}

export default Nav;
